import React from 'react'
import logo from "./../images/Aggie_Fangs_Logo_Transparent.png";
//import Checkbox from "./Checkbox";

var probName = "";
var probConcepts = "";
var probPrompt = "";
var probInOut = "";

function getProblem () {
    // todo
    // this function will find the current problem and change what the page will output accordingly
    probName = "String Reversal";
    probConcepts = "Traversal of Lists or Strings, Basic Problem Solving";
    probPrompt = "Given a string, write a function that reverses the string such that the old first element is now the last element, the old second element is now the second to last element, etc.";
    probInOut = "In: apple\nOut: elppa\nIn: howdy\nOut: ydwoh";
}

function changeProblem () {
    alert("Selected a problem");
}

const Coding = () => {
    getProblem();
  return (
    <div>
        <div class="coding coding-header">
            <div class="split coding-logo">
                <img src={logo} alt="logo"/>
            </div>
            <div class="split coding-problem-name">
                <h1>Problem Name: {probName}</h1>
                <h2>Concepts Covered: {probConcepts}</h2>
            </div>
        </div>
        <div class="coding coding-main">
            <div class="split problem-list">
                <h1>Problems:</h1>
                <label>
                    <input type="checkbox"/> Sort By Company
                </label>
                <ul>
                    <li><a onClick={changeProblem()}>String Reversal</a></li>
                </ul>
            </div>
            <div class="split coding-problem">

            </div>
        </div>
    </div>
  )
}

export default Coding
